import { Component, OnInit } from '@angular/core';
import { SSOLoginDataModel } from '../../Models/SSOLoginDataModel';
import { PlacementDashService } from '../../Services/PlacementDashboard/PlacementDash.service';
import { ToastrService } from 'ngx-toastr';
import { LoaderService } from '../../Services/Loader/loader.service';
import { FormBuilder } from '@angular/forms';
import { ActivatedRoute, Router } from '@angular/router';
import { NgbModal } from '@ng-bootstrap/ng-bootstrap';

@Component({
  selector: 'app-placement-dashboard',
  templateUrl: './placement-dashboard.component.html',
  styleUrls: ['./placement-dashboard.component.css']
})
export class PlacementDashboardComponent implements OnInit {

  public viewPlacementDashboardList: any = [];
  public Table_SearchText: string = "";
  /*  public searchRequest = new CommonSubjectMasterSearchModel();*/
  public sSOLoginDataModel = new SSOLoginDataModel();
  public State: number = 0;
  public Message: string = '';
  public ErrorMessage: string = '';
  constructor(private PlacementDashService: PlacementDashService, private toastr: ToastrService, private loaderService: LoaderService, private formBuilder: FormBuilder, private activatedRoute: ActivatedRoute, private routers: Router, private modalService: NgbModal) {

  }

  async ngOnInit() {

    this.sSOLoginDataModel = await JSON.parse(String(localStorage.getItem('SSOLoginUser')));

    await this.GetAllData();


  }
  async GetAllData() {
    try {
      this.loaderService.requestStarted();
      await this.PlacementDashService.GetAllData()
        .then((data: any) => {
          data = JSON.parse(JSON.stringify(data));
      
          this.viewPlacementDashboardList = data['Data'];
          console.log(this.viewPlacementDashboardList);
        }, (error: any) => console.error(error)
        );
    }
    catch (ex) {
      console.log(ex);
    }
    finally {
      setTimeout(() => {
        this.loaderService.requestEnded();
      }, 200);
    }
  }
  
}
