<!-- Page Title Start -->
<div class="flex items-center justify-between flex-wrap gap-2 mb-6">
  <div>
    <h4 class="text-slate-900 text-lg font-medium mb-2">Student</h4>
    <div class="md:flex hidden items-center gap-3 text-sm font-semibold">
      <a href="#" class="text-sm font-medium text-slate-700">Masters</a>
      <i class="material-symbols-rounded text-xl flex-shrink-0 text-slate-500">chevron_right</i>
      <a href="#" class="text-sm font-medium text-slate-700" aria-current="page">Student List</a>
    </div>
  </div>
  <!--<a href="#" class="btn bg-info text-white btn-sm">Add Form</a>-->
</div>
<!-- Page Title End -->
<div class="card">
  <div class="card-body p-5">

    <div class="flex justify-between flex-wrap items-center mb-3 p-3 py-2 bg-light">
      <b class="text-1xl-5"><i class="ti ti-list"></i>Search</b>
    </div>
    <form [formGroup]="PlacementSelectedListStudentForm">
      <div class="grid 2xl:grid-cols-4 xl:grid-cols-4 lg:grid-cols-3 md:grid-cols-2 grid-cols-1 gap-3">

        <div>
          <label class="text-gray-800 text-sm font-medium inline-block mb-1">Campus</label>
          <select formControlName="CampusPostID" class="form-select" id="CampusPostID" [(ngModel)]="CampusPostID" (change)="GetCampusWiseHiringRoleDDL()">
            <option value="0">--Select--</option>
            <option *ngFor="let item of CampusMasterList" [value]="item.ID">{{item.Name}}</option>
          </select>
          <div *ngIf="isSubmitted && PlacementSelectedListStudentForm.get('CampusPostID').errors" class="text-danger">
            <p *ngIf="!PlacementSelectedListStudentForm.get('CampusPostID').errors.required"><i aria-hidden="true"></i>This field is required.</p>
          </div>
        </div>
        <div>
          <label class="text-gray-800 text-sm font-medium inline-block mb-1">Branch<span class="StarMarking">*</span></label>
          <select formControlName="BranchID" class="form-select" id="BranchID" [(ngModel)]="BranchID">
            <option value="0">--Select--</option>
            <option *ngFor="let item of StreamMasterList" [value]="item.StreamID">{{item.StreamName}}</option>
          </select>
          <div *ngIf="isSubmitted && PlacementSelectedListStudentForm.get('BranchID').errors" class="text-danger">
            <p *ngIf="!PlacementSelectedListStudentForm.get('BranchID').errors.required"><i aria-hidden="true"></i>This field is required.</p>
          </div>
        </div>

        <div>
          <label class="text-gray-800 text-sm font-medium inline-block mb-1">Hiring Role<span class="StarMarking">*</span></label>
          <select formControlName="HiringRoleID" style="min-width:200px !important;" class="form-select" id="HiringRoleID" [(ngModel)]="HiringRoleID">
            <option value="0">--Select--</option>
            <option *ngFor="let item of HiringRoleMasterList" [value]="item.ID">{{item.Name}}</option>
          </select>

        </div>
        <div>
          <button type="button" class="btn bg-success text-white mt-6" (click)="GetAllData()">Search</button>
        </div>

      </div>
    </form>
    <div class="flex justify-between flex-wrap items-center mb-3 p-3 py-2 bg-light mt-4">
      <b class="text-1xl-5"><i class="ti ti-list"></i>All ShortListed Student List</b>
    </div>

    <div class="grid 2xl:grid-cols-4 xl:grid-cols-4 lg:grid-cols-3 md:grid-cols-2 grid-cols-1 gap-3">
      <div class="">
        <select id="ddlConter" class="form-select  couterDropDown">
          <option value="50">50</option>
          <option value="100">100</option>
          <option value="500">500</option>
          <option value="1000">1000</option>
        </select>
      </div>
      <div class="">
      </div>
      <div class="">
      </div>
      <div class="flex items-center">
        <input class="form-input" placeholder="Search..." [(ngModel)]="Table_SearchText" />
        <button type="button" class="btn bg-success text-white" (click)="ExcelExport()">Export</button>
      </div>
    </div>
    <div class="overflow-x-auto mt-3">
      <table class="table table-border w-full table-auto" cellpadding="5" id="tbl_placementStudent">
        <thead class="bg-light/40 border-b border-gray-100 dark:bg-light/5 dark:border-b-gray-700 h-10">
          <tr>
            <th class="text-left">Sr. No.</th>
            <th align="left">
              <input type="checkbox" id="checkboxSeletedAl" class="checkboxSize" [checked]="AllSelect"
                     #checkboxSeletedAll (change)="checkboxthView_checkboxchange(checkboxSeletedAll.checked)">
            </th>
            <th class="text-left">Student Name </th>
            <th class="text-left">Hiring Role</th>
            <th class="text-left">Email Address</th>
            <th class="text-left">Father's Name</th>
            <th class="text-left">DOB  </th>
            <th class="text-left">Age  </th>
            <th class="text-left">Gender   </th>
            <th class="text-left">Mobile   </th>
            <th class="text-left">Home District</th>
            <th class="text-left">State</th>
            <th class="text-left">Institute Name   </th>
            <th class="text-left">Institute Type</th>
            <th class="text-left">Enrollment No</th>


          </tr>
        </thead>
        <tbody>
          <tr *ngFor="let row of StudentList | tableSearchFilter:Table_SearchText;let idx=index;">
            <td>{{idx+1}}</td>
            <td>

              <input type="checkbox" name="MarkCheck" [checked]="true"
                     [(ngModel)]="row.Marked" />
            </td>
            <td class="text-left">{{row.StudentName}} </td>
            <td class="text-left">{{row.HiringRoleName}} </td>
            <td class="text-left">{{row.Email}}</td>
            <td class="text-left">{{row.FatherName}}</td>
            <td class="text-left">{{row.DOB}}</td>
            <td class="text-left">{{row.Age}}</td>
            <td class="text-left">{{row.Dis_Gender}}</td>
            <td class="text-left">{{row.MobileNo}}</td>
            <td class="text-left">{{row.DistrictName}}</td>
            <td class="text-left">{{row.StateName}}</td>
            <td class="text-left">{{row.InstituteName}}</td>
            <td class="text-left">{{row.InstitutionManagementType}}</td>
            <td class="text-left">{{row.EnrollmentNo}}</td>

          </tr>
        </tbody>
      </table>


    </div>
    <div class="grid 2xl:grid-cols-1 xl:grid-cols-1 lg:grid-cols-1 md:grid-cols-1 grid-cols-1 gap-3 mt-4">
      <div class="text-center">
        <button class="btn bg-success text-white" (click)="SaveAllData()" type="button">Submit</button>
      </div>
    </div>
  </div>
</div>
